    digitalWrite(7, HIGH);

    resetRFM69();
    radio.setCS(RFM69_NSS);
    radio.initialize(FREQUENCY,NODEID,NETWORKID);

    //radio.setHighPower(); //uncomment only for RFM69HW!

    radio.encrypt(KEY);
    char buff[50];
    sprintf(buff,  \nTransmitting at %d Mhz... , FREQUENCY==RF69_433MHZ ? 433 : FREQUENCY==RF69_868MHZ ? 868 : 915);
    Serial.println(buff);

    radio24.begin();
    // optionally, increase the delay between retries   # of retries
    radio24.setRetries(15,15);
    radio24.setChannel(119);
    // по умолчанию СЛУШАЕМ
    radio24.openWritingPipe(pipes[1]);
    radio24.openReadingPipe(1,pipes[0]);
    radio24.startListening();

    delay(20);

    attachInterrupt(0, isr_RF24, FALLING);

    irrecv.enableIRIn();

    Wire.begin();
    RTC.begin();

    if (! RTC.isrunning()) {
        Serial.println( RTC is NOT running! );
        // following line sets the RTC to the date   time this sketch was compiled
        RTC.adjust(DateTime(__DATE__, __TIME__));
    }
}

long lastPeriod = -1;
void loop() {

    //check for any received packets
    if (radio.receiveDone())
    {
        Serial.print( [ );
        Serial.print(radio.SENDERID, DEC);
        Serial.print( ]  );
        for (byte i = 0; i   radio.DATALEN; i++)
        Serial.print((char)radio.DATA[i]);
        Serial.print(    [RX_RSSI: );
        Serial.print(radio.readRSSI());
        Serial.print( ] );

        if (radio.ACK_REQUESTED)
        {
            radio.sendACK();
            Serial.print(  - ACK sent );
            delay(10);
        }
        Serial.println();
    }

    int currPeriod = millis()/TRANSMITPERIOD;
    if (currPeriod != lastPeriod)
    {
        //fill in the struct with new values
        theData.nodeId = NODEID;
        theData.uptime = millis();
        theData.temp = radio.readTemperature();//91.23; //it s hot!

        Serial.print( Sending struct ( );
        Serial.print(sizeof(theData));
        Serial.print(  bytes) ...  );
        if (radio.sendWithRetry(GATEWAYID, (const void*)( theData), sizeof(theData)))
        Serial.print(  ok! );
        else Serial.print(  nothing... );
        Serial.println();
        lastPeriod=currPeriod;
    }

    listenRF24();

    if (irrecv.decode( results)) {
        Serial.println(results.value, HEX);
        irrecv.resume(); // Receive the next value
        blinkStop=millis()+100;
        digitalWrite(LED, HIGH);
    }

    if (digitalRead(BUTTON)==LOW) {
        blinkStop=millis()+1000;
        digitalWrite(LED, HIGH);
    }

    if (millis() blinkStop) {
        digitalWrite(LED, LOW);
    }

    if(millis() timeReady){
        timeReady=millis()+2000;
        DateTime now = RTC.now();

        Serial.print(now.year(), DEC);
        Serial.print( / );
        Serial.print(now.month(), DEC);
        Serial.print( / );
        Serial.print(now.day(), DEC);
        Serial.print(   );
        Serial.print(now.hour(), DEC);
        Serial.print( : );
        Serial.print(now.minute(), DEC);
        Serial.print( : );
        Serial.print(now.second(), DEC);
