  myservo.attach(9);  // 把舵机连接到引脚 9 上
}

void loop() {
  for (pos = 0; pos  = 180; pos += 1) { // 从 0° 动作到 180°
    // in steps of 1 degree
    myservo.write(pos);              // 告诉舵机旋转到角度“pos”
    delay(15);                       // 等待 15ms 让舵机旋转到位
  }
  for (pos = 180; pos  = 0; pos -= 1) { // 从 0° 动作到 180°
    myservo.write(pos);              // 告诉舵机旋转到角度“pos”
    delay(15);                       // 等待 15ms 让舵机旋转到位
  }
